import React, { useState } from "react";
import { View, Text, StyleSheet } from "react-native";
import {
  Card,
  Title,
  Paragraph,
  Button,
  Appbar,
  Provider,
  Portal,
  Modal,
  Snackbar,
} from "react-native-paper";
import { DayEnum } from "../common/DayEnum";
import { FontAwesome } from "@expo/vector-icons";
import { Avatar } from "react-native-paper";

export default function SujectDetails({ route }) {
  const [visible, setVisible] = useState(false);

  const { item } = route.params;

  const showModal = () => setVisible(true);
  const hideModal = () => setVisible(false);
  console.log(route);

  return (
    <View style={styles.container}>
      <Card style={styles.card}>
        <Card.Content style={styles.card}>
          <FontAwesome name="book" size={24} color="black" />
          <Title style={styles.title}>Details of</Title>
          <Title style={styles.title}>{item.subject}</Title>
          <Paragraph>
            <Text style={styles.textBold}>Profesor name: </Text>
            {item.profesor}
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>Start Time: </Text>
            {item.startTime}
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>End adress: </Text>
            {item.endTime}
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>Day: </Text>
            {DayEnum[item.sheduleDay]}
          </Paragraph>

          <Button
            icon="delete"
            color="#7d0633"
            onPress={() => {
              showModal(true);
            }}
          />

          <Provider>
            <Portal>
              <Modal
                style={styles.modal}
                visible={visible}
                onDismiss={hideModal}
                contentContainerStyle={styles.modal}
              >
                <Text style={styles.modalText}>Confirm deletion of:</Text>
                <Text style={styles.modalTextItemName}>{item.subject}</Text>
                <Button
                  onPress={() => handleDelete()}
                  style={styles.buttonConfirm}
                >
                  <Text style={{ color: "white" }}>Delete</Text>
                </Button>
              </Modal>
            </Portal>
          </Provider>
        </Card.Content>
      </Card>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    backgroundColor: "#e3e3e3",
  },
  card: {
    justifyContent: "center",
    marginLeft: "auto",
    marginRight: "auto",
    width: "100%",
    height: "100%",
  },
  modal: {
    backgroundColor: "white",
    padding: 10,
    height: "50%",
  },
  modalText: {
    marginLeft: "auto",
    marginRight: "auto",
    fontSize: 20,
  },
  modalTextItemName: {
    marginLeft: "auto",
    marginRight: "auto",
    textAlign: "center",
    color: "#7d0633",
    fontSize: 35,
  },
  title: {
    textAlign: "center",
    marginBottom: 15,
  },
  textBold: {
    fontWeight: "bold",
  },
  chip: {
    marginLeft: "auto",
    marginRight: "auto",
    backgroundColor: "#006494",
  },
  activityIndicator: {
    width: "100%",
    height: "80%",
    justifyContent: "center",
  },
  buttonConfirm: {
    marginTop: 15,
    backgroundColor: "#7d0633",
    width: "100%",
  },
  snackbar: {
    backgroundColor: "#7d0633",
  },
});
