import React, { useEffect, useState } from "react";
import { Alert, StyleSheet, Text, View, Picker } from "react-native";
import { TextInput } from "react-native-paper";
import { FontAwesome } from "@expo/vector-icons";
import { useForm, Controller } from "react-hook-form";
import { Button, Modal, Portal, Provider } from "react-native-paper";
import { Fontisto } from "@expo/vector-icons";
import { getAllGroups } from "../api/getAllGroups";

export default function Register({ navigation }) {
  const [selectedValue, setSelectedValue] = useState();
  const [visible, setVisible] = useState(false);
  const [groups, setGroups] = useState();
  const showModal = () => setVisible(true);
  const hideModal = () => setVisible(false);
  const onSubmit = (data) => console.log(data);
  const { control, handleSubmit, errors } = useForm();

  useEffect(() => {
    getAllGroups().then((response) => setGroups(response.data));
  }, [groups]);

  return (
    <View style={styles.container}>
      <Text style={styles.text}>
        Sign up <Fontisto name="unlocked" size={24} color="black" />
      </Text>
      <Controller
        control={control}
        render={({ onChange, onBlur, value }) => (
          <TextInput
            style={styles.input}
            label="Email"
            mode="outlined"
            onBlur={onBlur}
            onChangeText={(value) => onChange(value)}
            value={value}
          />
        )}
        name="email"
        // rules={{ required: true, pattern: /^\S+@\S+\.\S+$/ }}
        defaultValue=""
      />
      {errors.email && (
        <Text style={styles.errorMessage}>Email is required.</Text>
      )}
      <Controller
        control={control}
        render={({ onChange, onBlur, value }) => (
          <TextInput
            style={styles.input}
            label="First Name"
            mode="outlined"
            onBlur={onBlur}
            onChangeText={(value) => onChange(value)}
            value={value}
          />
        )}
        name="firstName"
        // rules={{ required: true }}
        defaultValue=""
      />
      {errors.email && (
        <Text style={styles.errorMessage}>Email is required.</Text>
      )}
      <Controller
        control={control}
        render={({ onChange, onBlur, value }) => (
          <TextInput
            style={styles.input}
            label="Last Name"
            mode="outlined"
            onBlur={onBlur}
            onChangeText={(value) => onChange(value)}
            value={value}
          />
        )}
        name="lastName"
        // rules={{ required: true }}
        defaultValue=""
      />
      {errors.email && (
        <Text style={styles.errorMessage}>Email is required.</Text>
      )}
      <Controller
        control={control}
        render={({ onChange, onBlur, value }) => (
          <TextInput
            style={styles.input}
            label="Password"
            mode="outlined"
            onBlur={onBlur}
            onChangeText={(value) => onChange(value)}
            value={value}
          />
        )}
        name="password"
        rules={{ required: true }}
        defaultValue=""
      />
      {errors.password && (
        <Text style={styles.errorMessage}>Password is required.</Text>
      )}

      <Controller
        control={control}
        render={({ onChange, onBlur, value }) => (
          <TextInput
            disabled
            style={styles.disabledInput}
            label="Group"
            mode="outlined"
            onBlur={onBlur}
            onChangeText={(value) => onChange(value)}
            value={selectedValue}
          />
        )}
        name="group"
        rules={{ required: true }}
        defaultValue=""
      />
      {errors.group && (
        <Text style={styles.errorMessage}>Group is required.</Text>
      )}

      <Provider>
        <Portal>
          <Modal
            visible={visible}
            onDismiss={hideModal}
            contentContainerStyle={styles.modal}
          >
            <Controller
              control={control}
              render={({ onChange, onBlur, value }) => (
                <Picker
                  selectedValue={selectedValue}
                  style={styles.picker}
                  onValueChange={(itemValue, itemIndex) => {
                    onChange(itemValue);
                    setSelectedValue(itemValue);
                    console.log(itemValue);
                  }}
                >
                  <Picker.Item label="Java" value="Java" />
                  <Picker.Item label="JavaScript" value="JavaScript" />
                  <Picker.Item label="C#" value="C#" />
                  <Picker.Item label="Test" value="Test" />
                </Picker>
              )}
              name="group"
              // rules={{ required: true }}
              defaultValue=""
            />
          </Modal>
        </Portal>
        <Button onPress={showModal}>Choose Group</Button>
        <Button
          mode="contained"
          style={styles.button}
          title="Submit"
          onPress={handleSubmit(onSubmit)}
        >
          Sign Up
        </Button>
      </Provider>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: "100%",
    backgroundColor: "#e3e3e3",
  },
  text: {
    textAlign: "center",
    fontSize: 30,
    marginTop: 20,
    marginBottom: 10,
  },
  icon: {
    textAlign: "center",
  },
  errorMessage: {
    marginLeft: 12,
    color: "red",
  },
  button: {
    marginTop: 15,
    backgroundColor: "#006494",
    alignContent: "center",
    marginLeft: "auto",
    marginRight: "auto",
    width: "95%",
  },
  signUp: {
    marginTop: 15,
    marginLeft: "auto",
    marginRight: "auto",
  },
  input: {
    backgroundColor: "white",
    height: 40,
    padding: 10,
    borderRadius: 4,
  },
  disabledInput: {
    backgroundColor: "white",
    height: 40,
    padding: 10,
  },
  modal: {
    backgroundColor: "white",
    padding: 10,
  },
});
