import * as React from "react";
import { Appbar } from "react-native-paper";
import {
  Alert,
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { Chip } from "react-native-paper";
import { List } from "react-native-paper";
import { useState, useEffect } from "react";

export const Calendar = () => {
  const [subcjetsList, setSubjectList] = useState();
  const [day, setDay] = useState("Monday");

  useEffect(() => {
    if (day == "Monday") {
      setSubjectList(list1);
    } else if (day == "Tuesday") {
      setSubjectList(list2);
    }
  }, [day]);
  //   useEffect(() => {
  //     switch (day) {
  //       case "Monday":
  //         setSubjectList(list1);
  //       case "Tuesday":
  //         setSubjectList(list2);
  //       case "Wednesday":
  //         setSubjectList(list3);
  //     }
  //   }, [subcjetsList, day]);
  const list1 = [
    {
      title: "Zaawan. prog. internetowe wyk",
      prof: "Bartczuk Łukasz",
      type: "elerning",
      startTime: "9:00",
      endTime: "12:00",
    },
    {
      title: "Prog. urządzeń mobilnych wyk",
      prof: "Grosser Andrzej",
      type: "elerning",
      startTime: "12.00 ",
      endTime: "15.00",
    },
  ];
  const list2 = [
    {
      title: "Test 2",
      prof: "test test",
      type: "elerning",
      startTime: "9:00",
      endTime: "12:00",
    },
    {
      title: "Test 2",
      prof: "test test",
      type: "elerning",
      startTime: "12.00 ",
      endTime: "15.00",
    },
  ];
  const list3 = [];

  return (
    <>
      <Appbar.Header style={styles.appBar}>
        <Chip
          style={styles.chip}
          textStyle={{ fontSize: 10 }}
          onPress={() => {
            setDay("Monday");
          }}
        >
          Monday
        </Chip>
        <Chip
          style={styles.chip}
          textStyle={{ fontSize: 10 }}
          onPress={() => setDay("Tuesday")}
        >
          Tuesday
        </Chip>
        <Chip
          style={styles.chip}
          textStyle={{ fontSize: 10 }}
          onPress={() => setDay("Wednesday")}
        >
          Wednesday
        </Chip>
        <Chip
          style={styles.chip}
          textStyle={{ fontSize: 10 }}
          onPress={() => console.log("Thursday")}
        >
          Thursday
        </Chip>
        <Chip
          style={styles.chip}
          textStyle={{ fontSize: 10 }}
          onPress={() => console.log("Friday")}
        >
          Friday
        </Chip>
      </Appbar.Header>
      <SafeAreaView style={styles.scrollConteriner}>
        <ScrollView>
          <List.Section>
            {subcjetsList?.map((item, key) => (
              <List.Item
                key={key}
                title={`${item.title}`}
                description={`hours: ${item.startTime} - ${
                  item.endTime
                } ${"\n"} prof: ${item.prof} `}
                left={(props) => <List.Icon {...props} icon="folder" />}
              />
            ))}
          </List.Section>
        </ScrollView>
      </SafeAreaView>
    </>
  );
};

const styles = StyleSheet.create({
  appBar: { justifyContent: "center", backgroundColor: "white" },
  chip: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  scrollConteriner: {
    height: "100%",
  },
});
