import React, { useEffect, useState } from "react";
import {
  View,
  KeyboardAvoidingView,
  TextInput,
  StyleSheet,
  Text,
  Platform,
  TouchableWithoutFeedback,
  Keyboard,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { List, Avatar, Button } from "react-native-paper";
import { useForm, Controller } from "react-hook-form";
import { getMessages } from "../api/getMessages";
import { sendMessage } from "../api/sendMessage";
import { userIdSelector } from "../store/selectors/globalSelector";
import { useSelector } from "react-redux";
import { updateGroupSelector } from "../store/selectors/globalSelector";
import { FontAwesome } from "@expo/vector-icons";

export default function Messenger() {
  const [messages, setMessages] = useState();
  const { control, handleSubmit, errors, reset } = useForm();
  const [clear, setClear] = useState("");

  const userId = useSelector(userIdSelector);
  const groupId = useSelector(updateGroupSelector);

  const onSubmit = (data) => {
    reset({ content: "" });

    data.userId = userId;
    data.groupId = groupId;
    // sendMessage(data).then(response =>{

    // })
    console.log(data);
  };

  useEffect(() => {
    getMessages(1).then((response) => {
      console.log(response.data);
      setMessages(response.data);
    });
  }, []);
  return (
    <KeyboardAvoidingView
      keyboardVerticalOffset={60}
      contentContainerStyle={{ marginTop: 2 }}
      behavior={Platform.OS == "ios" ? "position" : "height"}
      style={styles.container}
    >
      <SafeAreaView style={styles.scrollConteriner}>
        <ScrollView>
          <List.Section>
            {messages?.map((item, key) =>
              item.user.userId == userId ? (
                <List.Item
                  titleStyle={{ textAlign: "right", marginRight: 10 }}
                  descriptionStyle={styles.descriptionLoggedUser}
                  descriptionNumberOfLines={100}
                  title={`${item.user.firstName} ${item.user.lastName}`}
                  description={item.content}
                  right={(props) => (
                    <Avatar.Icon
                      style={styles.avatarLoggedUser}
                      size={30}
                      icon="account"
                    />
                  )}
                />
              ) : (
                <List.Item
                  descriptionStyle={styles.description}
                  descriptionNumberOfLines={100}
                  key={key}
                  title={`${item.user.firstName} ${item.user.lastName}`}
                  description={item.content}
                  left={(props) => (
                    <Avatar.Icon
                      style={styles.avatar}
                      size={30}
                      icon="account"
                    />
                  )}
                />
              )
            )}
          </List.Section>
          {/* <List.Section>
            <List.Item
              titleStyle={{ textAlign: "right", marginRight: 10 }}
              descriptionStyle={styles.descriptionLoggedUser}
              descriptionNumberOfLines={100}
              title={"You"}
              description={
                "Contrary to popular beliefes of the word in classical"
              }
              right={(props) => (
                <Avatar.Icon
                  style={styles.avatarLoggedUser}
                  size={30}
                  icon="account"
                />
              )}
            />
            <List.Item
              title={"Jan Nowak"}
              descriptionStyle={styles.description}
              descriptionNumberOfLines={100}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              left={(props) => (
                <Avatar.Icon style={styles.avatar} size={30} icon="account" />
              )}
            />
          </List.Section> */}
        </ScrollView>
      </SafeAreaView>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <View style={styles.inner}>
          <Controller
            control={control}
            render={({ onChange, onBlur, value }) => (
              <TextInput
                style={styles.textInput}
                label="Message"
                mode="outlined"
                onBlur={handleSubmit(onSubmit)}
                onChangeText={(value) => onChange(value)}
                value={value}
              />
            )}
            name="content"
            rules={{ required: true }}
            defaultValue={clear}
          />
          <Button style={styles.button}>
            <FontAwesome name="send" size={24} color="black" />
          </Button>
        </View>
      </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#e3e3e3",
  },
  inner: {
    padding: 24,
    flex: 1,
    flexDirection: "row",
  },
  textInput: {
    backgroundColor: "white",
    height: 50,
    padding: 10,
    width: 200,
    borderRadius: 10,
  },
  button: {
    backgroundColor: "red",
    width: "100%",
  },
  scrollConteriner: {
    height: "87%",
    backgroundColor: "#e3e3e3",
  },
  btnContainer: {
    backgroundColor: "white",
    marginTop: 12,
  },
  avatar: {
    backgroundColor: "#006494",
  },
  avatarLoggedUser: {
    backgroundColor: "#7d0633",
  },
  avatar: {
    backgroundColor: "#006494",
  },
  description: {
    marginTop: 16,
    textAlign: "center",
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#006494",
    borderRadius: 18,
    color: "#20232a",
  },
  descriptionLoggedUser: {
    marginTop: 16,
    textAlign: "center",
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#7d0633",
    borderRadius: 18,
    color: "#20232a",
  },
});
