import React, { useEffect, useState } from "react";
import {
  View,
  KeyboardAvoidingView,
  TextInput,
  StyleSheet,
  Text,
  Platform,
  TouchableWithoutFeedback,
  Button,
  Keyboard,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { List, Avatar } from "react-native-paper";
import { getShedulePerDay } from "../api/getShedulePerDay";
import { useForm, Controller } from "react-hook-form";

export default function Messenger() {
  const [subcjetsList, setSubjectList] = useState();
  const { control, handleSubmit, errors } = useForm();

  useEffect(() => {
    getShedulePerDay(1, 1).then((response) => {
      setSubjectList(response.data);
      console.log(response.data);
    });
  }, []);
  return (
    <KeyboardAvoidingView
      keyboardVerticalOffset={60}
      contentContainerStyle={{ marginTop: 2 }}
      behavior={Platform.OS == "ios" ? "position" : "height"}
      style={styles.container}
    >
      <SafeAreaView style={styles.scrollConteriner}>
        <ScrollView>
          <List.Section>
            <List.Item
              titleStyle={{ textAlign: "right", marginRight: 10 }}
              descriptionStyle={styles.descriptionLoggedUser}
              descriptionNumberOfLines={100}
              title={"You"}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              right={(props) => (
                <Avatar.Icon
                  style={styles.avatarLoggedUser}
                  size={30}
                  icon="account"
                />
              )}
            />
            <List.Item
              title={"Jan Nowak"}
              descriptionStyle={styles.description}
              descriptionNumberOfLines={100}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              left={(props) => (
                <Avatar.Icon style={styles.avatar} size={30} icon="account" />
              )}
            />
            <List.Item
              titleStyle={{ textAlign: "right", marginRight: 10 }}
              descriptionStyle={styles.descriptionLoggedUser}
              descriptionNumberOfLines={100}
              title={"You"}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              right={(props) => (
                <Avatar.Icon
                  style={styles.avatarLoggedUser}
                  size={30}
                  icon="account"
                />
              )}
            />
            <List.Item
              title={"Jan Nowak"}
              descriptionStyle={styles.description}
              descriptionNumberOfLines={100}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              left={(props) => (
                <Avatar.Icon style={styles.avatar} size={30} icon="account" />
              )}
            />
            <List.Item
              titleStyle={{ textAlign: "right", marginRight: 10 }}
              descriptionStyle={styles.descriptionLoggedUser}
              descriptionNumberOfLines={100}
              title={"You"}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              right={(props) => (
                <Avatar.Icon
                  style={styles.avatarLoggedUser}
                  size={30}
                  icon="account"
                />
              )}
            />
          </List.Section>
        </ScrollView>
      </SafeAreaView>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <View style={styles.inner}>
          <Controller
            control={control}
            render={({ onChange, onBlur, value }) => (
              <TextInput
                style={styles.textInput}
                label="First Name"
                mode="outlined"
                onBlur={onBlur}
                onChangeText={(value) => onChange(value)}
                value={value}
              />
            )}
            name="firstName"
            rules={{ required: true }}
            defaultValue=""
          />
        </View>
      </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  inner: {
    padding: 24,
    flex: 1,
  },
  textInput: {
    backgroundColor: "white",
    height: 60,
    padding: 10,
    borderRadius: 10,
    backgroundColor: "#006494",
  },
  scrollConteriner: {
    height: "85%",
  },
  btnContainer: {
    backgroundColor: "white",
    marginTop: 12,
  },
  avatar: {
    backgroundColor: "#006494",
  },
  avatarLoggedUser: {
    backgroundColor: "#7d0633",
  },
  avatar: {
    backgroundColor: "#006494",
  },
  description: {
    marginTop: 16,
    textAlign: "center",
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#006494",
    borderRadius: 18,
    color: "#20232a",
  },
  descriptionLoggedUser: {
    marginTop: 16,
    textAlign: "center",
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#7d0633",
    borderRadius: 18,
    color: "#20232a",
  },
});
