import React, { useEffect, useState } from "react";
import {
  View,
  KeyboardAvoidingView,
  TextInput,
  StyleSheet,
  Text,
  Platform,
  TouchableWithoutFeedback,
  Button,
  Keyboard,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { List, Avatar } from "react-native-paper";
import { getShedulePerDay } from "../api/getShedulePerDay";
import { useForm, Controller } from "react-hook-form";

export default function Messenger() {
  const [subcjetsList, setSubjectList] = useState();
  const { control, handleSubmit, errors } = useForm();

  useEffect(() => {
    getShedulePerDay(1, 1).then((response) => {
      setSubjectList(response.data);
      console.log(response.data);
    });
  }, []);
  return (
    <KeyboardAvoidingView
      behavior={Platform.OS == "ios" ? "padding" : "height"}
      style={styles.container}
    >
      <SafeAreaView style={styles.scrollConteriner}>
        <ScrollView>
          <List.Section>
            <List.Item
              title={"Jan Nowak"}
              descriptionStyle={styles.description}
              descriptionNumberOfLines={100}
              description={
                "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical"
              }
              left={(props) => <Avatar.Icon size={24} icon="folder" />}
            />
          </List.Section>
        </ScrollView>
      </SafeAreaView>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <View style={styles.inner}>
          <TextInput placeholder="Username" style={styles.textInput} />
          <View style={styles.btnContainer}>
            <Button title="Submit" onPress={() => null} />
          </View>
        </View>
      </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  inner: {
    padding: 24,
    flex: 1,
    justifyContent: "space-around",
  },
  header: {
    fontSize: 36,
    marginBottom: 48,
  },
  textInput: {
    height: 40,
    borderColor: "#000000",
    borderBottomWidth: 1,
    marginBottom: 36,
  },
  scrollConteriner: {
    height: "70%",
  },
  btnContainer: {
    backgroundColor: "white",
    marginTop: 12,
  },
  description: {
    marginTop: 16,
    textAlign: "center",
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#006494",
    borderRadius: 18,
    color: "#20232a",
  },
});
