import React, { useEffect, useState } from "react";
import {
  Alert,
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { List, TextInput, Avatar } from "react-native-paper";
import { getShedulePerDay } from "../api/getShedulePerDay";

export default function Messenger() {
  const [subcjetsList, setSubjectList] = useState();
  const [text, setText] = React.useState("");

  useEffect(() => {
    getShedulePerDay(1, 1).then((response) => {
      setSubjectList(response.data);
      console.log(response.data);
    });
  }, []);
  return (
    <>
      <Text>Messenger Page</Text>
      <>
        <SafeAreaView style={styles.scrollConterinerDown}>
          <ScrollView>
            <List.Section>
              <List.Item
                title={`Jan Nowak`}
                descriptionNumberOfLines={100}
                descriptionStyle={styles.description}
                description={`Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.`}
                left={(props) => (
                  <Avatar.Icon style={styles.avatar} size={40} icon="account" />
                )}
              />
              <List.Item
                title={`Jan Nowak`}
                descriptionNumberOfLines={100}
                descriptionStyle={styles.description}
                description={`Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.`}
                left={(props) => (
                  <Avatar.Icon style={styles.avatar} size={40} icon="account" />
                )}
              />

              <List.Item
                titleStyle={{ textAlign: "right", marginRight: 10 }}
                title="You"
                descriptionNumberOfLines={100}
                descriptionStyle={styles.description}
                description={`Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.`}
                right={(props) => (
                  <Avatar.Icon style={styles.avatar} size={40} icon="account" />
                )}
              />
            </List.Section>
          </ScrollView>
        </SafeAreaView>
        <TextInput
          label="Email"
          value={text}
          scrollConterinerUp={() => console.log("hello")}
          onChangeText={(text) => setText(text)}
        />
      </>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "grey",
  },
  avatar: {
    backgroundColor: "#006494",
    color: "red",
  },
  description: {
    marginTop: 16,
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: "#006494",
    borderRadius: 18,
    color: "#20232a",
    textAlign: "center",
  },
  scrollConterinerDown: {
    height: "85%",
  },
  scrollConterinerUp: {
    height: "125%",
  },
});
