import * as React from "react";
import { View, Text, StyleSheet } from "react-native";
import { Card, Title, Paragraph } from "react-native-paper";

export default function SujectDetails({ route }) {
  console.log(route);
  return (
    <View style={styles.container}>
      <Text style={styles.infoText}>Details of</Text>
      <Text style={styles.modalText}>{route.params.item.subject}</Text>

      <Card style={styles.card}>
        <Card.Content style={styles.card}>
          <Avatar.Icon
            style={styles.avatar}
            size={100}
            icon="account-card-details"
          />
          <Title style={styles.title}>User Profile</Title>
          <Paragraph>
            <Text style={styles.textBold}>First name: </Text>
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>Last name: </Text>
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>Email adress: </Text>
          </Paragraph>
          <Paragraph>
            <Text style={styles.textBold}>Assigned to group: </Text>
          </Paragraph>
        </Card.Content>
      </Card>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    backgroundColor: "#e3e3e3",
  },
  modal: {
    backgroundColor: "white",
    padding: 10,
    height: "50%",
  },
  modalText: {
    marginLeft: "auto",
    marginRight: "auto",
    fontSize: 20,
  },
  modalTextItemName: {
    marginLeft: "auto",
    marginRight: "auto",
    textAlign: "center",
    color: "#7d0633",
    fontSize: 35,
  },
  infoText: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  chip: {
    marginLeft: "auto",
    marginRight: "auto",
    backgroundColor: "#006494",
  },
  activityIndicator: {
    width: "100%",
    height: "80%",
    justifyContent: "center",
  },
  buttonConfirm: {
    marginTop: 15,
    backgroundColor: "#7d0633",
    width: "100%",
  },
  snackbar: {
    backgroundColor: "#7d0633",
  },
});
